mapper.select:
select *,(select name as discount_name from ll_customer_discount_group where id=t.discount) discount_name from ll_supplier t 

	
		and name like concat('%', #{name}, '%')
	
	
		and status = #{status}
	
	
		and type=#{type}
	
	
		and parent_customer_id = #{parent_customer_id}
	
	
		and customer_id=#{customer_id}
	
	
		and id = #{id}
	
	
		and id in (${ids})
	
	
		and openid = #{openid}
	

 ${orderby}

mapper.update:
update ll_supplier
customer_id=#{customer_id}, parent_customer_id=#{parent_customer_id}, level=#{level}, parent_name=#{parent_name}, name=#{name}, status=#{status}, type=#{type}, issale=#{issale}, address=#{address}, balance=#{balance}, points=#{points}, deposit_money=#{deposit_money}, create_time=#{create_time}, contacts_name=#{contacts_name}, contacts_phone=#{contacts_phone}, remark=#{remark}, modify_time=#{modify_time}, discount=#{discount}, pay_where = #{pay_where}, supplier_img = #{supplier_img}, personal_pay_img = #{personal_pay_img}, charge_rate = #{charge_rate},
where id=#{id}

mapper.select:
select t.*,(select name from ll_supplier_type where id = t.parentid) parentName from ll_supplier_type t 

	and t.id = #{id}
	and t.parentid = #{parentid}
	and t.level = #{level}

order by t.seq desc
