mapper.select:
select count(1) from ll_activity_user_relation t 
where t.activity_config_id = #{activity_config_id}
and t.join_date = #{join_date}

select t.*,c.activity_type from ll_activity_user_relation t,ll_activity_conf c

	c.id = t.activity_config_id
	 and t.id = #{id}	
	 and user_id = #{user_id}	
	 and openid = #{openid}	
	 and t.status = #{status}	
	 and activity_config_id = #{activity_config_id}	
	 and join_date = #{join_date}	
	 and category = #{category}	
	 and activity_time = #{activity_time}	
	 and order_code = #{order_code}	
	 and phone = #{phone}

select t.* from ll_activity_user_relation t

	 and user_id = #{user_id}	
	 and openid = #{openid}	
	 and t.status = #{status}	
	 and activity_config_id = #{activity_config_id}	
	 and join_date = #{join_date}	
	 and category = #{category}	
	 and activity_time = #{activity_time}	
	 and order_code = #{order_code}	
	 and phone = #{phone}

select t.*,c.activity_type from ll_activity_user_relation t,ll_activity_conf c

	c.id = t.activity_config_id
	 and t.id = #{id}	
	 and user_id = #{user_id}	
	 and openid = #{openid}	
	 and t.status = #{status}	
