select * from ll_activity_img 

	 and user_id = #{user_id}
	 and activity_config_id = #{activity_config_id}
	 and weixin_id = #{weixin_id}
	 and id = #{id}

select * from ll_activity_win_his

	and user_id=#{user_id}
	and openid=#{openid}
	and activity_config_id=#{activity_config_id}
	and prize_id=#{prize_id}
	and activity_name=#{activity_name}
	and user_name=#{user_name}
	and category = #{category}
	and status = #{status}
	and status in (${arrstatus})
	and win_time>=#{beginWinTime}
	and win_time<=#{endWinTime}

select * from ll_activity_product_relation

real_num != 0 
	 and id = #{id}
	 and activity_id = #{activity_id}
	 and prize_id = #{prize_id}
	 and supplier_product_id = #{supplier_product_id}
	 and other_spid = #{other_spid}
	 and product_name = #{product_name}
	 and create_time = #{create_time}
	 and eff_time = #{eff_time}
	 and dff_time = #{dff_time}
	 and weight = #{weight}

mapper.update:
update ll_activity_user_relation
activity_time = #{activity_time} , product_id = #{product_id} , category = #{category} , modify_time = #{modify_time} , phone = #{phone} , status = #{status} , share_status = #{share_status} , user_ip = #{user_ip} , order_code = #{order_code} ,
id = #{id}

mapper.select:
select * from ll_activity_olympic_guess

	 and id = #{id} 
	 and user_id = #{user_id} 
	 and guess_date = #{guess_date} 
	 and status = #{status} 
	 and id = #{id} 
	 and win_num = #{win_num} 
	 and win_desc = #{win_desc}

mapper.update:
update ll_activity_olympic_guess
user_id = #{user_id} , guess_date = #{guess_date} , status = #{status} , win_num = #{win_num} , win_desc = #{win_desc} ,
where id = #{id}

update ll_activity_product_relation
weight = #{weight}, real_num = #{real_num},
where id = #{id}

update ll_activity_instance
activity_id = #{activity_id} , real_join_num = #{real_join_num} , name = #{name} , life_status = #{life_status} , max_join_num = #{max_join_num}, real_win_num = #{real_win_num}
where id = #{id}

mapper.select:
select *  from  ll_activity_instance 

 and id = #{id} 
	 and activity_id = #{activity_id} 
		 and  name = #{name} 
		  and life_status = #{life_status} 
		  and max_join_num = #{max_join_num} 
		  and start_hour = #{start_hour} 
		  and createdate = #{createdate} 
		  and code = #{code} 
		  and start_day = #{start_day} 
		  and frequency = #{frequency} 
		  and time = #{time} 

order by start_day,start_hour asc

select * from ll_activity_rule

activity_id = #{activity_id}
order by seq asc

