mapper.select:
select * from ll_shop_order_info 

	 and id = #{id} 
	 and user_id = #{user_id} 
	 and order_code like concat('%', #{order_code} ,'%')  
	 and status = #{status} 
	 and event_type = #{event_type} 
	 and is_sended = #{is_sended} 
	 and contact_phone like concat('%', #{contact_phone} ,'%') 
	 and contact_name like concat('%', #{contact_name} ,'%')  
	 and invoice_type = #{invoice_type} 
	 and status not in (${notInStatus})
	

	  ${orderby}

mapper.update:
update ll_shop_order_info
user_id = #{user_id} , nick_name = #{nick_name} , modify_time = #{modify_time} , order_name = #{order_name} , order_img = #{order_img} , cost_price = #{cost_price} , cost_points = #{cost_points} , cost_money = #{cost_money} , cost_balance = #{cost_balance} , coupon_total_price = #{coupon_total_price} , status = #{status} , remark = #{remark} , event_type = #{event_type} , lucky_money = #{lucky_money} , share_time = #{share_time} , share_type = #{share_type} , is_sended = #{is_sended} , express_price = #{express_price} , contact_name = #{contact_name} , contact_phone = #{contact_phone} , contact_address = #{contact_address} , express_mode = #{express_mode} , express_date = #{express_date} , express_time = #{express_time} , express_order_code = #{express_order_code} , invoice_type = #{invoice_type} , invoice_info = #{invoice_info} ,
and id = #{id} and order_code = #{order_code}

mapper.select:
select sop.*,p.head_img from ll_shop_order_product sop ,ll_supplier_product sp, ll_product p
where sop.supplier_product_id = sp.id and sp.product_id = p.id
	
	 and sop.id = #{id} 
	 and sop.order_info_id = #{order_info_id} 
	 and sop.order_code = #{order_code} 
	 and sop.user_id = #{user_id} 
	 and sop.supplier_id = #{supplier_id} 
	 and sop.supplier_product_id = #{supplier_product_id} 
	
	  ${orderby}

