mapper.update:
update ll_supplier_product
store_num=#{store_num}, product_price=#{product_price}, product_real_price=#{product_real_price}, product_type=#{product_type}, product_type_name=#{product_type_name}, product_brand=#{product_brand}, product_brand_name=#{product_brand_name}, discount=#{discount}, eff_time=#{eff_time}, dff_time=#{dff_time}, modify_time=#{modify_time}, modify_admin=#{modify_admin}, min_luckymoney = #{min_luckymoney}, max_luckymoney = #{max_luckymoney}, product_offset_points = #{product_offset_points}, package_num = #{package_num},
where id = #{id}

mapper.select:
select sp.*,(select name from ll_supplier where id = sp.supplier_id) supplier_name,p.product_limit_points ,p.give_points ,p.head_img from ll_supplier_product sp, ll_product p 

	sp.product_id = p.id 
	 and sp.supplier_id = #{supplier_id}
	 and sp.customer_id = #{customer_id}
	 and exists( select 1 from ll_product p where p.status = #{status} and p.id = sp.product_id )
	 and #{product_brand_name} = sp.product_brand_name 
	 and #{product_brand} = sp.product_brand 
	 and #{product_type_name} = sp.product_type_name 
	 and #{product_type} = sp.product_type 
	 and #{product_province} = p.product_province 
	 and sp.store_num >= ${store_num} 
	 and sp.product_id = #{product_id}
	 and sp.id = #{id}
	 and #{now_time} >= sp.eff_time and sp.dff_time >= #{now_time}
	order by sp.product_type

select * from ll_supplier_product sp

	 and sp.supplier_id = #{supplier_id}
	 and sp.customer_id = #{customer_id}
	 and exists( select 1 from ll_product p where p.status = #{status} and p.id = sp.product_id )
	 and #{product_brand_name} = sp.product_brand_name 
	 and #{product_type_name} = sp.product_type_name 
	 and sp.store_num >= #{store_num} 
	 and sp.product_id = #{product_id}
	 and sp.id = #{id}
	order by sp.product_price

select * from ll_supplier_product_attach

	 and id = #{id} 
	 and supplier_product_id = #{supplier_product_id} 
	 and attach_supplier_product_id = #{attach_supplier_product_id} 
	 and customer_id = #{customer_id}

		select sp.*,(select name from ll_supplier where id = sp.supplier_id) supplier_name,p.product_limit_points ,p.give_points ,p.head_img from ll_supplier_product sp, ll_product p
		where p.id = sp.product_id and sp.id in
		
            #{id}
