mapper.select:
select * from ll_supplier_event

	
		and title like concat('%', #{title}, '%')
	
	
		and content like concat('%', #{content}, '%')
	
	
		and status = #{status}
	
	
		and supplier_id = #{supplier_id}
	
	
		and searchName = #{searchName}
	
	
		and type = #{type}
	

 ${orderby}

select * from ll_supplier_event

	 and title like concat('%', #{title}, '%') 
	 and content like concat('%', #{content}, '%') 
					and status = #{status}			
					and supplier_id = #{supplier_id}			
					and searchName = #{searchName}			
					and type = #{type}			
	 and end_time >= #{end_time} 

 ${orderby}

select * from ll_supplier_event

	 and title like concat('%', #{title}, '%') 
	 and content like concat('%', #{content}, '%') 
					and status = #{status}			
					and supplier_id = #{supplier_id}			
					and searchName = #{searchName}			
					and type = #{type}			
	 and #{end_time} >= end_time 

 ${orderby}

select *,(select head_img from ll_wechat_user x where x.id = userId ) head_img from ll_supplier_event_code 

	 and id = #{id} 
	 and eventId = #{eventId} 
	 and userId in (select u2.id from ll_wechat_user u1,ll_wechat_user u2 where u1.id = #{userId} 
							and (u2.unionid = u1.unionid  or u2.id = #{userId} )) 
	 and fromUserId = #{fromUserId} 
	 and code = #{code} 

order by id

select count(1) from ll_supplier_event_code 

	 and id = #{id} 
	 and eventId = #{eventId} 
	 and userId = #{userId} 
	 and fromUserId = #{fromUserId} 
	 and code = #{code} 

order by id
