mapper.select:
select *, a_name as 'text', 'javascript:updateUserData(2,' + convert(varchar ,a_id) +',"'+ a_name +'");'  as 'href'  from area 

	
	 and a_id = #{a_id}
	 and a_companyid = #{a_companyid}

mapper.update:
update area
a_companyid = #{a_companyid}, a_name = #{a_name}, a_updatedate = #{a_updatedate},
a_id = #{a_id}

update ll_address
name = #{name}, full_name = #{full_name}, level = #{level}, parent_id = #{parent_id}, update_time = #{update_time}, comment = #{comment}, status = #{status}, code = #{code},
id = #{id} and company_id = #{company_id}

mapper.select:
		select *,a.name text, isnull(cast(a.parent_id as varchar ),'#' )  parent from ll_address a 

		
			 and id = #{id}
			 and name like '%'+ #{name}+ '%'
			 and full_name like '%'+ #{full_name}+ '%'
			 and company_id = #{company_id}
			 and parent_id = #{parent_id}
