mapper.select:
select cm.*,u.u_no user_no,u.u_name user_name, u.u_address user_address ,u.u_phone user_phone, o.o_name operator_name from changeMeter cm ,userInfo u ,operator o

	cm.cm_userid = u.u_id and cm.cm_operatorid = o.o_id 
	 and cm_id = #{cm_id}
	 and cm_type = #{cm_type}
	 and u.u_no = #{user_no}
	 and u.u_name like '%' + #{user_name} + '%'
	 and u.u_phone like '%' + #{user_phone} + '%'
	

 ${orderby}

mapper.update:
update changeMeter
cm_userid = #{cm_userid}, cm_oldmetercode = #{cm_oldmetercode}, cm_newmetercode = #{cm_newmetercode}, cm_happendate = #{cm_happendate}, cm_type = #{cm_type}, cm_status = #{cm_status}, cm_operatorid = #{cm_operatorid}, cm_remark = #{cm_remark}, cm_oldmeterno = #{cm_oldmeterno}, cm_newmeterno = #{cm_newmeterno},
cm_id = #{cm_id}
