mapper.select:
select * from ll_wechat_user 

	
		and nick_name like concat('%',#{nick_name},'%')
	
	
		and sex = #{sex}
	
	
		and status = #{status}
	
	
		and parentid = #{parentid}
	
	
		and openid like concat('%',#{openid},'%')
	
	
		and unionid = #{unionid}
	
	
	 and id = #{id}

mapper.insert:
insert into ll_wechat_user(
openid, create_time, nick_name, phone, sex, province, city, status, expire_date, access_token, token_end_time, parentid, level, offline_num, join_img_num, join_offline_num, winning_num, head_img, last_access_time, sence_type, senceid, points, balance, last_order_time, unionid, originalid,
)values(
#{openid}, #{create_time}, #{nick_name}, #{phone}, #{sex}, #{province}, #{city}, #{status}, #{expire_date}, #{access_token}, #{token_end_time}, #{parentid}, #{level}, #{offline_num}, #{join_img_num}, #{join_offline_num}, #{winning_num}, #{head_img}, #{last_access_time}, #{sence_type}, #{senceid}, #{points}, #{balance}, #{last_order_time}, #{unionid}, #{originalid},
)

mapper.update:
update ll_wechat_user
openid = #{openid}, create_time = #{create_time}, nick_name = #{nick_name}, phone = #{phone}, sex = #{sex}, province = #{province}, city = #{city}, status = #{status}, expire_date = #{expire_date}, access_token = #{access_token}, token_end_time = #{token_end_time}, parentid = #{parentid}, level = #{level}, offline_num = #{offline_num}, join_img_num = #{join_img_num}, join_offline_num = #{join_offline_num}, winning_num = #{winning_num}, head_img = #{head_img}, points = #{points}, balance = #{balance}, last_order_time = #{last_order_time}, type = #{type}, share_type = #{share_type}, share_time = #{share_time}, unionid = #{unionid}, originalid = #{originalid}
id = #{id}

mapper.select:
select * from ll_wechat_user_qrcode 

	 and openId = #{openId}
	 and senceCode = #{senceCode}
	 and senceId = #{senceId}
	 and id = #{id}

select sum(p.productRealPrice) as winCash from product p,latestlottery l where l.productId = p.productId 
and l.userId=#{userId}
and l.announcedTime>=#{startTime}
and l.announcedTime<#{endTime}

select sum(sbp.spellbuyPrice) as winMoneyCount from spellbuyproduct sbp,latestlottery l where l.spellbuyProductId = sbp.spellbuyProductId
and l.userId=#{userId}
and l.announcedTime>=#{startTime}
and l.announcedTime<#{endTime}
