sql.where:
m.userid = u.id 
 and m.systemid = #{systemid}
 and m.productid = #{productid}
 and m.userid = #{userid}
 and m.code = #{code}
 and u.name like '%${userName}%'
 and u.area1 like '%${area1}%'
 and u.area2 like '%${area2}%'
 and u.area3 like '%${area3}%'
 and u.area4 like '%${area4}%'
 and u.addressid = #{addressid}
 and ispid = #{ispid}
