sql.where:
m.userid = u.id 
 and m.systemid = #{systemid}
 and m.productid = #{productid}
 and m.userid = #{userid}
 and m.code = #{code}
 and u.code = #{userCode}
 and u.name like '%${userName}%'
 and u.area1 like '%${area1}%'
 and u.area2 like '%${area2}%'
 and u.area3 like '%${area3}%'
 and u.area4 like '%${area4}%'
 and u.addressid = #{addressid}
 and ispid = #{ispid}

mapper.select:
select count(distinct meterid ) from lm_record r 

	 and r.curtime like '${day}%' 
	 and r.systemid = #{systemid}

select ca.value ,ifnull(count(DISTINCT p.id),0) thisMonthData , (select ifnull(count(DISTINCT pa.id),0) from lm_meter pa where CONCAT(SUBSTR(DATE_SUB(CURDATE(), INTERVAL 1 MONTH),1,8),ca.value) >= pa.opentime and pa.changed = 0 and pa.opened = 1 and pa.systemid = #{systemid} ) lastMonthData
from ll_conf_attribute ca LEFT JOIN lm_meter p on CONCAT(SUBSTR(CURDATE(),1,8),ca.value ) >= p.opentime and p.changed = 0 and p.opened = 1 and p.systemid = #{systemid}
where ca.code = 'day_of_month' group by ca.value order by ca.value
