sql.where:
m.userid = u.id and u.status != -1
 and m.systemid = #{systemid}
 and m.productid = #{productid}
 and u.name like '%${name}%'
 and u.area1 like '%${area1}%'
 and u.area2 like '%${area2}%'
 and u.area3 like '%${area3}%'
 and u.area4 like '%${area4}%'
 and u.id  = #{id}
 and u.code like '%${code}%'
 and u.addressid = #{addressid} 
 and u.idcard like '%${idcard}%'
 and u.phone like '%${phone}%'
 and m.code = #{metercode}
 and m.opened = #{meterOpened}
 and m.pricecode = #{priceCode}
 and m.factorycode = #{factoryCode}
